import React from 'react';
import axios from 'axios';

export default class AddComment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            content: "",
            comments: [{comment: ''}]
        };
    }

        onChangeComment = (e) => {
        this.setState({
            content: e.target.value
        });
    }

        onSubmit = (e) => {
        e.preventDefault()

        const comments = {
            content: this.state.content,
            comments: this.state.comments
        }

        axios.post('http://localhost:4000/comments/create-comment', comments)
            .then(res => console.log(res.data));

        //axios.post('http://localhost:4000/articles/create-article', comments)
            //.then(res => console.log(res.data));

        this.setState({
            comments: comments.concat([{comment: this.state.content}])
        })
    }


    render() {
        return (
            <form onSubmit ={this.onSubmit}>

                <p>Add Comment:</p>
                <input
                    type='text'
                    name='content'
                    onChange = {this.onChangeComment}
                    value={this.state.content}
                />
                    <button className="submitButton" type="submit">Add Comment</button>

            </form>

                        
        );
    }
}
