import React from 'react';
import axios from 'axios';

export default class AddComment extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            comment: "",
            comments: []
        };
    }

        onChangeComment = (e) => {
        this.setState({
            comment: e.target.value
        });
    }

        onSubmit = (e) => {
        e.preventDefault()

        const comment = {
            comment: this.state.comment,
        }

        axios.post('http://localhost:4000/articles/create-article', comment)
            .then(res => console.log(res.data));

        this.setState(state =>{
            const comments = state.comments.concat(state.comment);
            return{
                comments
            }
        })
    }


    render() {
        return (
            <form onSubmit ={this.onSubmit}>

                <p>Add Comment:</p>
                <input
                    type='text'
                    name='comment'
                    onChange = {this.onChangeComment}
                    value={this.state.comment}
                />
                    <button className="submitButton" type="submit">Add Comment</button>

            </form>

                        
        );
    }
}
