import React from 'react';

export default class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            title: "",
            content: "",
            errormessage: ""
           
            
           
        };
    }

     

    myChangeHandler = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        let errEm = '';
        let errTitle = '';
        let errContent = '';
        let reg = /^[a-z\d]+[\w\d.-]*@(?:[a-z\d]+[a-z\d-]+\.){1,5}[a-z]{2,6}$/i;

        if (name === "email") {
            if (name !== reg) {
                errEm = <strong>Please enter valid email</strong>;

            }
            this.setState({errormessage: errEm});
        }
        
    

        if (name === "title"){
            if (value.length <= 2){
                errTitle = <strong>Your title must be more than two characters</strong>;
            }  
            this.setState({errormessage: errTitle});
        }

        
        if (name === "content"){
            if (value.length <= 100){
                errContent = <strong>Your content must be more than 100 characters</strong>;
            }  
            this.setState({errormessage: errContent});
        }

       

        this.setState({[name]: value});
    }

    render() {
        return (
            <form className="form">
                <p>Please enter your e-mail:</p>
                <input
                    type='text'
                    name='email'
                    onChange={this.myChangeHandler}
                />
                <p>Please enter title:</p>
                <input
                    type='text'
                    name='title'
                    onChange={this.myChangeHandler}
                />
                <p>Please enter content:</p>
                <textarea className="text-area"
                    type='text'
                    name='content'
                    onChange={this.myChangeHandler}
                />
                    <br/><br/>

                    <button className="submitButton">Submit</button>

                    <h4>{this.state.errormessage}</h4>

            </form>

                        
        );
    }
}
