import React, {Component} from "react"
import { Link } from 'react-router-dom'
import axios from 'axios'

export default class HomePageContent extends Component {

            constructor(props){
                super(props);
                this.state = {
                    commentContent: '',
                    comments: []
                }
            }
    
         addComment = (e) => {
        e.preventDefault()

        const comment = {
            commentContent : this.commentContent
        }

        axios.post('http://localhost:4000/articles/create-comment', comment)
            .then(res => console.log(res.data));

        this.setState({
            commentContent: ''
        })
    }

     deleteComment = (e) => {
        e.preventDefault();
        axios.delete('http://localhost:4000/articles/delete-comment/' + this.props.obj._id)
            .then((res) => {
                console.log('Deleted!')
            }).catch((error) => {
                console.log(error)
        })
        this.forceUpdate();
    }

        componentDidMount() {
        axios.get('http://localhost:4000/articles/')
            .then(res => {
                this.setState({
                    comment: res.data
                });
            })
            .catch(error => {
                console.log(error)
            })
    }

         dataTable() {
        return this.state.comments.map((res, i) => {
            return <this.props.obj.comment obj={res} key={i}/>;
        });
    }

    render(){
        return (
        
                    <div className="article-box">
                        <article class="article">
                            <div className="article-title">
                                <h1>{this.props.obj.title}</h1>
                            </div>
                            <div className="article-content">
                                {this.props.obj.content}
                            </div>
                            <div className = "email">
                                {this.props.obj.email}
                            </div>
                            <div>   
                                <form onChange = {this.addComment}>
                                <p>Add Comment:</p>
                                <input
                                 type='text'
                                 name='comment'
                                 value={this.state.comment}
                                />
                                <button className="submitButton" type="submit">Add Comment</button>
                                <button onClick={this.deleteComment}>Delete</button>

                                </form>
                            </div>
                        </article>
                    </div>
    
                )
            }
}
