import React from 'react'

export default class Home extends React.Component {

        constructor(props){
            super(props)

            this.state = {
                counter: 0
            }
        }

        componentWillMount ()  {
           if(this.state.counter === 0 || this.state.counter > 0) {this.setState({
                counter: this.state.counter + 1
                });
           }
            document.cookie = 'Liczba odwiedzin=' + this.state.counter;

            this.state.counter.preventDefault()
        }

    render(){

        return(
            <div>
            <h1>Artyku≈Çy</h1>
            <p>Licznik: {this.state.counter}</p>
            </div>
        )
    }
}
