import React from 'react'
import axios from 'axios'
import SingleComment from './SingleComment'

class Comments extends React.Component{
        constructor(props){
            super(props)

            this.state = {
                Comment: '',
                setComment: ''
            }
        }

        handleChange = (e) => {
            this.setState({setComment: e.target.value})
        }

    

        onSubmit = (e) => {
        e.preventDefault();

        const variables = {
            comment: this.state.Comment,
            postId: this.props.postId
        }

        axios.post('http://localhost:4000/comments/create-comment', variables)
         .then(res => console.log(res.data));

         this.setState({setComment:''})
    }

    render() {
    return (
        <div>
            <hr />
            <form className='comment-box' onSubmit={this.onSubmit}>
            <input type='textarea' className='comment'
            onChange={this.handleChange}
            value={this.state.Comment}
            placeholder='Add comment'
            />
            <br />
            <button className='comment-button' onClick={this.onSubmit}>Add Comment</button>
            </form>
    
            {console.log(this.props.commentsList)}

           {/* {this.props.commentsList && this.props.commentsList.map((comment, index) =>(
                <SingleComment comment={comment} postId={this.props.postId} refreshFunction={this.props.refreshFunction}/> 
            ))}*/}
        </div>
    )
    }
}
       
export default Comments;
